import React, { useState, useRef } from 'react';

function DodajUzytkownika() {
  const [password, setPassword] = useState("");
  const passwordRef = useRef(); // Utwórz referencję

  const generatePassword = () => {
    const length = 20;
    const charset = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*";
    let retVal = "";
    for (let i = 0, n = charset.length; i < length; ++i) {
      retVal += charset.charAt(Math.floor(Math.random() * n));
    }
    setPassword(retVal);
  };

  const handleUserInput = (e) => {
    setPassword(e.target.value);
  };

  const handleCopy = () => {
    passwordRef.current.select(); // Wybierz tekst
    document.execCommand("copy"); // Skopiuj
  };

  return (
  <>
  <div className='dodajUzytkownika'>
    <div className='COL11'>
        <div>Imię</div>
        <div>Nazwisko</div>
        <div>Data urodzenia</div>
        <div>PESEL</div>
        <div>Adres zamieszkania</div>
        <div>Adres adres korespondencyjny</div>
        <div className='myContainer'>
          <div>Hasło</div>
          <div></div>
          <button onClick={handleCopy}>Kopiuj</button> {/* Dodaj przycisk do kopiowania hasła */}
        </div>
        <div className='haslo2'>Powtórz hasło</div>
      </div>

      <div className='COL22'>
        <input></input>
        <input></input>
        <input></input>
        <input></input>
        <input></input>
        <input></input>
        <input value={password} type="password" ref={passwordRef} onChange={handleUserInput} />
        <input type="password"></input>

      </div>
      <div className='generujHasloDiv'>
        <div className='odw'>

          <button onClick={generatePassword}>Generuj hasło</button>
        </div>
      </div>
    </div>
    <div>
      <button>Dodaj</button>
    </div>
  </>
  );
};

export default DodajUzytkownika;
