import React, { useRef, useEffect, useState } from 'react';

const GRID_SIZE = 20;
const TILE_SIZE = 20;

function drawIsometricTile(context, x, y, isHovered) {
  const isoX = (x - y) * TILE_SIZE;
  const isoY = (x + y) * TILE_SIZE / 2 - (isHovered ? 10 : 0);

  context.beginPath();
  context.moveTo(isoX, isoY);
  context.lineTo(isoX + TILE_SIZE, isoY + TILE_SIZE / 2);
  context.lineTo(isoX, isoY + TILE_SIZE);
  context.lineTo(isoX - TILE_SIZE, isoY + TILE_SIZE / 2);
  context.closePath();

  context.fillStyle = isHovered ? '#f00' : ((x + y) % 2 === 0 ? '#ccc' : '#999');
  context.fill();
}

function CanvasComponent() {
  const canvasRef = useRef(null);
  const [hoveredTile, setHoveredTile] = useState(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    context.translate(canvas.width / 4, 0);

    const redraw = () => {
      context.clearRect(-canvas.width / 4, 0, canvas.width, canvas.height);
      for (let x = 0; x < GRID_SIZE; x++) {
        for (let y = 0; y < GRID_SIZE; y++) {
          drawIsometricTile(context, x, y, hoveredTile && hoveredTile.x === x && hoveredTile.y === y);
        }
      }
    }

    const handleMouseMove = (e) => {
      const rect = canvas.getBoundingClientRect();
      const scaleX = canvas.width / rect.width;
      const scaleY = canvas.height / rect.height;
      const canvasX = (e.clientX - rect.left) * scaleX - canvas.width / 4;
      const canvasY = (e.clientY - rect.top) * scaleY;
      const x = Math.floor((canvasX / TILE_SIZE + canvasY / (TILE_SIZE / 2)) / 2);
      const y = Math.floor((canvasY / (TILE_SIZE / 2) - canvasX / TILE_SIZE) / 2);
      setHoveredTile({ x, y });
    }

    canvas.addEventListener('mousemove', handleMouseMove);
    redraw();
    return () => canvas.removeEventListener('mousemove', handleMouseMove);
  }, [hoveredTile]);

  return (
    <div className='mainCanvasWindow'>
      <canvas
        ref={canvasRef}
        width={GRID_SIZE * TILE_SIZE * 2}
        height={GRID_SIZE * TILE_SIZE}
      />
    </div>
  );
}

export default CanvasComponent;
