import React, { useState } from 'react';
import Draggable from 'react-draggable'; // <--- importowanie biblioteki
import './App.css';

const Siatka = () => {
  const [divNumbers, setDivNumbers] = useState({});

  const handleClick = key => {
    setDivNumbers(prevState => ({
      ...prevState,
      [key]: Math.floor(Math.random() * 10+1)
    }));
  };

  const grid = [];

  for (let i = 0; i < 10; i++) {
    for (let j = 0; j < 10; j++) {
      const key = `${i}-${j}`;
      grid.push(
        <Draggable key={key}>  {/* Komponent Draggable, który umożliwia przesuwanie */}
          <div 
            onClick={() => handleClick(key)}
            style={{
              width: '50px',
              height: '50px',
              border: '1px solid black',
              backgroundColor: '#eee',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center'
            }}
          >
            {divNumbers[key]}
          </div>
        </Draggable>
      );
    }
  }

  return (
    <div className="grid" style={{userSelect: 'none'}}>
      {grid}
    </div>
  );
};

export default Siatka;
