import React, { useState, useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import './App.css';

function* numberGenerator(min = 1, max = 49, numbersToDisplay = 5) {
  let numbers = Array.from({length: max - min + 1}, (_, i) => i + min);
  for(let i = numbers.length - 1; i > 0; i--){
    const j = Math.floor(Math.random() * (i + 1));
    [numbers[i], numbers[j]] = [numbers[j], numbers[i]];
  }

  let currentIndex = 0;
  while (true) {
    yield numbers.slice(currentIndex, currentIndex + numbersToDisplay);
    currentIndex = (currentIndex + 1) % numbers.length;
  }
}

const generator = numberGenerator();

const Div = ({ startAnimation, animationDuration, handleSliderChange }) => {
  const [numbers, setNumbers] = useState(generator.next().value);
  const animRef = useRef(null);

  useEffect(() => {
    if (startAnimation) {
      setNumbers(generator.next().value);
      gsap.fromTo(animRef.current, { y: 50 }, { y: -50, duration: animationDuration, clearProps:"all" });
    }
  }, [startAnimation, animationDuration]);

  return (
    <div style={{ margin: '20px' }}>
      <div>Speed Break</div>
      <input 
        className='rangeSpeed'
        type="range" 
        min="1" 
        max="5" 
        step="0.1" 
        value={animationDuration} 
        onChange={handleSliderChange} 
        orient="vertical"
        style={{writingMode: 'bt-lr'}}
      />
      <div style={{ width: '50px', height: '250px', border: '1px solid black', backgroundColor: '#eee', display: 'flex', justifyContent: 'center', alignItems: 'center', overflow: 'hidden' }}>
        <div 
          ref={animRef}
          style={{ position: 'relative'}}
        >
          {numbers.map(number => <div key={number}>{number}</div>)}
        </div>
      </div>
    </div>
  );
};

const Losowanie = () => {
  const [startAnimation, setStartAnimation] = useState(false);
  const [animationDuration, setAnimationDuration] = useState(Number(localStorage.getItem('animationDuration')) || 3);
  const [speedBreak, setSpeedBreak] = useState(Number(localStorage.getItem('speedBreak')) || 3000);

  const handleSpeedBreakChange = (e) => {
    const newSpeedBreak = Number(e.target.value);
    setSpeedBreak(newSpeedBreak); 
    localStorage.setItem('speedBreak', newSpeedBreak);
  };
  
  const handleSliderChange = (e) => {
    const newAnimationDuration = Number(e.target.value);
    setAnimationDuration(newAnimationDuration);
    localStorage.setItem('animationDuration', newAnimationDuration);
  }

  const handleClick = () => {
    setStartAnimation(prev => !prev);
  }

  useEffect(() => {
    let timeout = setTimeout(() => {
      setStartAnimation(false);
    }, animationDuration * speedBreak);

    return () => clearTimeout(timeout);
  }, [startAnimation, animationDuration, speedBreak]); 

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <h1>Losowanie Lotto</h1>
      <div className="siemaE" style={{ display: 'flex',  }}>
        <Div
          startAnimation={startAnimation}
          animationDuration={animationDuration}
          handleSliderChange={handleSliderChange}
        />
      </div>
      <input 
        type="range" 
        min="1000" 
        max="20000" 
        step="1000" 
        value={speedBreak} 
        onChange={handleSpeedBreakChange} 
      />
      <button onClick={handleClick}>Losuj liczby</button>
    </div>
  );
};

export default Losowanie;
