import './App.css';
import React, { useState } from "react";
import jwtDecode from 'jwt-decode';  // dodane
import MainWindow from './MainWindow/MainWindow';
import Logowanie from './logowanie/logowanie';

function App() {
  const [loggedIn, setLoggedIn] = useState(JSON.parse(localStorage.getItem("loggedIn")) || false);
  const [serverMessage, setServerMessage] = useState(null);
  const [decodedToken, setDecodedToken] = useState(null); // dodane

  const handleLogin = (message, token) => { // dodane token
    setLoggedIn(true);
    setServerMessage(message);
    localStorage.setItem("loggedIn", true);
    const decoded = jwtDecode(token);  // dekodowanie tokena
    setDecodedToken(decoded);
    console.log(decodedToken); // ustawianie zdekodowanego tokena
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setServerMessage(null);
    localStorage.removeItem("loggedIn");
    setDecodedToken(null); // resetowanie zdekodowanego tokena po wylogowaniu
  };

  return loggedIn 
    ? <MainWindow handleLogout={handleLogout} serverMessage={serverMessage} decodedToken={decodedToken} /> // przekazanie decodedToken jako prop
    : <Logowanie handleLogin={handleLogin} />
}

export default App;
