import React, { useState, useEffect } from 'react';
import './App.css';

// Speed of animation in milliseconds
const animationSpeed = 0.15;

// Komponent Div
const Div = ({ startAnimation, number, animationDuration, minNumber, maxNumber }) => {
  const generateShuffledNumbers = (min, max) => {
    let numbers = Array.from({length: max - min + 1}, (_, i) => i + min);
    for(let i = numbers.length - 1; i > 0; i--){
      const j = Math.floor(Math.random() * (i + 1));
      [numbers[i], numbers[j]] = [numbers[j], numbers[i]];
    }
    return numbers;
  };

  const [displayedNumbers, setDisplayedNumbers] = useState(generateShuffledNumbers(minNumber, maxNumber));

  useEffect(() => {
    const shuffledNumbers = generateShuffledNumbers(minNumber, maxNumber);
    if (startAnimation) {
      setDisplayedNumbers(shuffledNumbers);
    } else {
      setDisplayedNumbers([
        shuffledNumbers[0],
        number, 
        shuffledNumbers[0]
      ]);
    }
  }, [startAnimation, number, minNumber, maxNumber]);

  return (
    <div 
      style={{
        width: '50px',
        height: '50px',
        border: '1px solid black',
        backgroundColor: '#eee',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden',
      }}
    >
      <div 
        className={startAnimation ? 'number-spin' : ''} 
        style={{ 
          position: 'relative',
          animationDuration: `${animationDuration}s`,
        }}
      >
        {displayedNumbers.map((num, index) => (
          <div key={index} style={{ marginBottom: '2px' }}>
            {num}
          </div>
        ))}
      </div>
    </div>
  );
};

// Komponent Siatka
// Komponent Siatka
const Siatka = () => {
  const [startAnimation, setStartAnimation] = useState(false);
  const [numbers, setNumbers] = useState([]);
  const [activeNumber, setActiveNumber] = useState(0);
  const [animationDuration, setAnimationDuration] = useState(2);
  const [minNumber, setMinNumber] = useState(1);  // Minimum number
  const [maxNumber, setMaxNumber] = useState(49);  // Maximum number
  const grid = [];

  const handleClick = () => {
    const nums = new Set();
    while(nums.size < 6) {
      nums.add(Math.floor(Math.random() * (maxNumber - minNumber + 1) + minNumber));
    }
    setNumbers([...nums]);
    setStartAnimation(true);
  }

  useEffect(() => {
    if (!startAnimation) return;
  
    let timeout;
    for (let i = 0; i < 6; i++) {
      timeout = setTimeout(() => {
        setActiveNumber(i);
        if (i === 5) {
          setStartAnimation(false);
        }
      }, i * 2500); // Zwiększono czas trwania animacji
    }
  
    return () => clearTimeout(timeout);
  }, [startAnimation]);

  for (let i = 0; i < 6; i++) {
    grid.push(
      <Div
        key={i}
        startAnimation={startAnimation && i === activeNumber}
        number={numbers[i]}
        animationDuration={animationDuration}
        minNumber={minNumber}
        maxNumber={maxNumber}
      />
    );
  }

  const handleSliderChange = (e) => {
    setAnimationDuration(Number(e.target.value));
  }

  return (
    <div className="grid" style={{userSelect: 'none', display: 'flex', gap: '10px', flexDirection: 'column'}}>
      <h1 style={{color: 'darkgreen', textAlign: 'center', margin: '20px 0'}}>Losowanie Lotto</h1>
      <div style={{display: 'flex', gap: '10px'}}>
        {grid}
        <button onClick={handleClick} style={{marginTop: '20px'}}>Losuj liczby</button>
        <input 
          type="range" 
          min="0.1" 
          max="0.8" 
          step="0.1" 
          value={animationDuration} 
          onChange={handleSliderChange} 
        />
      </div>
    </div>
  );
};


export default Siatka;
