import React, { useState, useEffect } from 'react';
import './App.css';

const Siatka = () => {
  const [divNumbers, setDivNumbers] = useState({});
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    if(isAnimating){
      const interval = setInterval(() => {
        const newNumbers = {};
        for(let i = 0; i < 10; i++){
          newNumbers[i] = Math.floor(Math.random() * 49 + 1);
        }
        setDivNumbers(newNumbers);
      }, 100);
      setTimeout(() => {
        clearInterval(interval);
        setIsAnimating(false);
      }, 3000);
    }
  }, [isAnimating]);

  const handleClick = () => {
    setIsAnimating(true);
  }

  const grid = [];

  for (let i = 0; i < 10; i++) {
    const key = i;
    grid.push(
      <div 
        key={key}
        style={{
          width: '50px',
          height: '50px',
          border: '1px solid black',
          backgroundColor: '#eee',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        {divNumbers[key]}
      </div>
    );
  }

  return (
    <div className="grid" style={{userSelect: 'none', display: 'flex', gap: '10px'}}>
      {grid}
      <button onClick={handleClick} style={{marginTop: '20px'}}>Losuj liczby</button>
    </div>
  );
};

export default Siatka;
