import React, { useState, useEffect } from 'react';
// import './SlotMachine.css';

function SlotMachine() {
  const [numbers, setNumbers] = useState([0, 0, 0]);
  const [spin, setSpin] = useState([false, false, false]);

  const generateNumber = (index) => {
    setSpin(prevSpin => {
      const newSpin = [...prevSpin];
      newSpin[index] = true;
      return newSpin;
    });

    setTimeout(() => {
      setNumbers(prevNumbers => {
        const newNumbers = [...prevNumbers];
        newNumbers[index] = Math.floor(Math.random() * 10);
        return newNumbers;
      });

      setSpin(prevSpin => {
        const newSpin = [...prevSpin];
        newSpin[index] = false;
        return newSpin;
      });
    }, 1000 + Math.random() * 1000); // Different durations for each number
  }

  const spinNumbers = () => {
    for (let i = 0; i < 3; i++) {
      generateNumber(i);
    }
  }

  return (
    <div className='slot-machine'>
      <button onClick={spinNumbers}>SPIN</button>
      <div className='number-display'>
        {numbers.map((number, index) => (
          <div key={index} className={`number ${spin[index] ? 'spin' : ''}`}>
            <div className='number-inner'>
              {number}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default SlotMachine;
