import React, { useState, useEffect } from 'react';
const URL = 'http://localhost:3001';

function Uzytkownicy() {
  // const [displayData, setDisplayData] = useState(false);
  // const [username, setUsername] = useState("ADMIN11");
  // const [password, setPassword] = useState("admino");
  // const [token, setToken] = useState("");
  const [data, setData] = useState([]); 
  const [page, setPage] = useState(1);
  const [pageNumber, setPageNumber] = useState(1);

  const fetchUsers = async (pageNumber) => {
    try {
      const response = await fetch(`${URL}/uzytkownicy?page=${pageNumber}`, {
        method: 'get',
        headers: { 'Content-Type': 'application/json' },
      });

      const data = await response.json();
      console.log(data)

      // setDisplayData(true);
      setData(data);
      return;
    } catch (error) {
      console.log(`Niepoprawne dane logowania: ${error.message}`);
    }
  };

  // Fetch users when component mounts or page changes
  useEffect(() => {
    fetchUsers(page);
  }, [page]);

  const nextPage = () => setPage(page + 1);
  const prevPage = () => setPage(page > 1 ? page - 1 : page);

  return (
    <div>
      <button onClick={() => fetchUsers(page)}>Szukaj</button>

      <button onClick={prevPage}>Poprzednia</button>
      <button onClick={nextPage}>Następna</button>

      {data && (
        <div className="vh80 divWithScroll">
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Age</th>
                <th>OPT</th>
              </tr>
            </thead>
            <tbody>
              {data.map((person, index) => (
                <tr key={index}>
                  <td>{person.id}</td>
                  <td>{person.user}</td>
                  <td>{person.age}</td>

                  <td>
                    <button>Edytuj</button>
                    <button>Usuń</button>
                    <button>Zablokuj</button>
                    <select>
                      <option>Dodaj karte</option>
                      <option>RFID</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Uzytkownicy;
