import React, { useRef, useEffect } from 'react';
import { gsap } from 'gsap';

const Losowanie = ({ startAnimation, number, animationDuration, minNumber, maxNumber, handleSliderChange, displayedNumbersProp }) => {
  const animRef = useRef(null);

  useEffect(() => {
    if (startAnimation) {
      gsap.to(animRef.current, {
        y: "-=50",
        duration: animationDuration,
        repeat: maxNumber - 1,
        onComplete: () => { 
          // Po zakończeniu animacji ustaw div z powrotem na początkową pozycję
          gsap.set(animRef.current, { y: "0" });
        },
      });
    }
  }, [startAnimation, animationDuration, maxNumber]);

  return (
    <div style={{ margin: '20px' }}>
      <div>Speed Break</div>
      <input 
        className='rangeSpeed'
        type="range" 
        min="0.1" 
        max="0.4" 
        step="0.1" 
        value={animationDuration} 
        onChange={handleSliderChange} 
        orient="vertical"
        style={{writingMode: 'bt-lr'}}
      />
      <div style={{ width: '50px', height: '50px', border: '1px solid black', backgroundColor: '#eee', display: 'flex', justifyContent: 'center', alignItems: 'center', overflow: 'hidden' }}>
        <div 
          ref={animRef}
          style={{ position: 'absolute'}}
        >
          {displayedNumbersProp.map((num, index) => (
            <div key={index} style={{ marginBottom: '2px' }}>{num}</div>
          ))}
        </div>
      </div>
    </div>
  );
};
export default Losowanie;
