import React, { useState } from 'react';
import Draggable from 'react-draggable'; // <--- importujemy bibliotekę
import './App.css';

const Siatka = () => {
  const [selectedDiv, setSelectedDiv] = useState(null);
  const grid = [];

  for (let i = 0; i < 10; i++) {
    for (let j = 0; j < 10; j++) {
      const key = `${i}-${j}`;
      grid.push(
        <div 
          key={key}
          style={{
            width: '50px',
            height: '50px',
            border: '1px solid black',
            backgroundColor: '#eee',
            boxShadow: selectedDiv === key ? '0 0 10px rgba(0,0,0,0.5)' : '',
            transition: 'boxShadow 0.3s ease'
          }}
          onClick={() => setSelectedDiv(key)}
        ></div>
      );
    }
  }

  const draggableDivs = [];

  for (let i = 0; i < 5; i++) {
    draggableDivs.push(
      <Draggable key={`draggable-${i}`}>
        <div
          style={{
            position: 'absolute',
            top: `${i * 60}px`,
            left: '520px',
            width: '50px',
            height: '50px',
            backgroundColor: 'green'
          }}
        >
        </div>
      </Draggable>
    );
  }

  return (
    <div className="grid">
      {grid}
      {draggableDivs}
    </div>
  );
};

export default Siatka;
