import { Routes, Route, useNavigate } from 'react-router-dom';
import Uzytkownicy from './Uzytkownicy';  // Zaimportuj komponent Uzytkownicy

export function checkRole() {
  const role = localStorage.getItem('role');
  return role === 'admin';
}

export const PrivateRoute = ({ path, ...props }) => {
  const navigate = useNavigate();
  const allowed = checkRole();

  if (!allowed) {
    navigate('/login');
  }

  return allowed ? <Routes><Route path={path} {...props} /></Routes> : null;
};
