import React, { useState, useEffect} from "react";
import logo from '../MainWindow/img/logo.png';
const URL = 'http://localhost:3001';// eslint-disable-next-line no-unused-vars



function Logowanie({ handleLogin }) {
  // const [username, setUsername] = useState("ADMIN11");
  // const [password, setPassword] = useState("admino");


  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");


  const [token, setToken] = useState("");
  const [serverMessage, setServerMessage] = useState("");

  useEffect(() => {
    setToken(generateToken());
  }, []);

  const generateToken = () => {
    return '_' + Math.random().toString(36).substr(2, 9);
  };

  // Obsługa logowania
  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch(`${URL}/logowanie`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ username, password, token })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Coś poszło nie tak');
      }
      console.log(data.message);
      // Zapisz token do stanu (lub do localStorage dla trwałości)
      setToken(data.token);
      setServerMessage(`${data.message}`);
      handleLogin(); // Przenieś wywołanie handleLogin() tutaj
      // alert(`Zalogowano pomyślnie! DATA:TOKEN-${data.token}`);

    } catch (error) {
      setServerMessage(`Niepoprawne dane logowania: ${error.message}`);
    }
  };








  return (
    <div id="LOGOWANIE">
      <div className="LG">
        <div className="logos">
          <img src={logo} alt="" className="logos-SWG" />
        </div>

        <div id="logowanie-s">
          <form onSubmit={handleSubmit}>
          <input
                type="hidden"
                id="token"
                autoComplete="off"
                name="token"
                placeholder="Token"
                value={token}
                className="w100"
              />

            <div id="name">
              <i className="material-icons prefix cwhite">apps</i>
              <input
                type="text"
                id="first"
                autoComplete="off"
                autoFocus="On"
                name="user"
                placeholder="Login"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="w100"
                // required
              />
            </div>

            <div id="pass">
              <i className="material-icons prefix cwhite">lock</i>
              <input
                type="password"
                id="second"
                autoComplete="off"
                name="pass"
                placeholder="Hasło"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w100"
                // required
              />
            </div>

            <div id="btn-log-in">
              <div className="wrapper">
                <div className="box">
                  <button type="submit" className="log-in" id="sub">
                    Sign In
                  </button>
                </div>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Logowanie;
