// Komponent PrivateRoute.js
import { Navigate } from "react-router-dom";

function PrivateRoute({ children }) {
  const role = localStorage.getItem('role');

  if (!role || role !== 'admin') {
    return <Navigate to="/" />; // przekierowanie do strony głównej, jeśli użytkownik nie jest adminem
  }

  return children; // renderowanie dzieci, jeśli użytkownik jest adminem
}

export default PrivateRoute;
