import React, { useState, useRef } from 'react';

function DodajUzytkownika() {
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [copySuccess, setCopySuccess] = useState(''); // Stan dla wiadomości po skopiowaniu
  const [isCopied, setIsCopied] = useState(false); // Stan dla przycisku po skopiowaniu
  const passwordRef = useRef();

  const generatePassword = () => {
    const length = 20;
    const charset = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*";
    let retVal = "";
    for (let i = 0, n = charset.length; i < length; ++i) {
      retVal += charset.charAt(Math.floor(Math.random() * n));
    }
    setPassword(retVal);
    setConfirmPassword(retVal);
    setIsCopied(false); // Resetuj stan przycisku po wygenerowaniu nowego hasła
  };

  const handleUserInput = (e) => {
    setPassword(e.target.value);
  };

  const handleConfirmPasswordInput = (e) => {
    setConfirmPassword(e.target.value);
  };

  const handleCopy = () => {
    passwordRef.current.select();
    document.execCommand("copy");
    setCopySuccess('Hasło skopiowane!'); // Ustaw wiadomość po skopiowaniu
    setIsCopied(true); // Wyłącz przycisk po skopiowaniu
  };

  return (
  <>
  <div className='dodajUzytkownika'>
    <div className='COL11'>
        <div>Imię</div>
        <div>Nazwisko</div>
        <div>Data urodzenia</div>
        <div>PESEL</div>
        <div>Adres zamieszkania</div>
        <div>Adres adres korespondencyjny</div>
        <div className='myContainer'>
          <div>Hasło</div>
          {(password && confirmPassword && password === confirmPassword) &&
            <div className='btn-kopiuj'>
              <button onClick={handleCopy} disabled={isCopied}>Kopiuj</button> {/* Wyłącz przycisk po skopiowaniu */}
              {copySuccess} {/* Wyświetl wiadomość po skopiowaniu */}
            </div>
          }
        </div>
        <div className='haslo2'>Powtórz hasło</div>
      </div>

      <div className='COL22'>
        <input></input>
        <input></input>
        <input></input>
        <input></input>
        <input></input>
        <input></input>
        <input value={password} type="password" ref={passwordRef} onChange={handleUserInput} />
        <input value={confirmPassword} type="password" onChange={handleConfirmPasswordInput} />

      </div>
      <div className='generujHasloDiv'>
        <div className='odw'>
          <button onClick={generatePassword}>Generuj hasło</button>
        </div>
      </div>
    </div>
    <div>
      <button>Dodaj</button>
    </div>
  </>
  );
};

export default DodajUzytkownika;
