import React, { useState } from 'react';

function DodajUzytkownika() {
  const [password, setPassword] = useState("");
  const [inputType, setInputType] = useState("password");
  const [isGenerated, setIsGenerated] = useState(false); // Nowy stan dla wygenerowanego hasła

  const generatePassword = () => {
    const length = 20;
    const charset = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*";
    let retVal = "";
    for (let i = 0, n = charset.length; i < length; ++i) {
      retVal += charset.charAt(Math.floor(Math.random() * n));
    }
    setPassword(retVal);
    setInputType("text");
    setIsGenerated(true); // Ustaw na true, gdy hasło zostanie wygenerowane
  };

  const handleUserInput = (e) => { // Dodaj event jako argument
    setPassword(e.target.value); // Ustaw wartość hasła na wprowadzoną przez użytkownika
    if (!isGenerated) { 
      setInputType("password");
    }
  };

  return (
  <>
  <div className='dodajUzytkownika'>
    <div className='COL11'>
        <div>Imię</div>
        <div>Nazwisko</div>
        <div>Data urodzenia</div>
        <div>PESEL</div>
        <div>Adres zamieszkania</div>
        <div>Adres adres korespondencyjny</div>
        <div>Hasło</div>
        <div>Powtórz hasło</div>
      </div>

      <div className='COL22'>
        <input></input>
        <input></input>
        <input></input>
        <input></input>
        <input></input>
        <input></input>
        <input type={inputType} value={password} onChange={handleUserInput}></input> {/* Dodaj obsługę zdarzenia onChange */}
        <input type={inputType} value={password}></input>

      </div>
      <div className='generujHasloDiv'>
        <button onClick={generatePassword}>Generuj hasło</button>
      </div>
    </div>
    <div>
      <button>Dodaj</button>
    </div>
  </>
  );
};

export default DodajUzytkownika;
