import React, { useState } from 'react';
import './App.css';

const Siatka = () => {
  const [divNumbers, setDivNumbers] = useState({});
  const [isAnimating, setIsAnimating] = useState(false);

  const handleClick = () => {
    setIsAnimating(true);
    const newNumbers = {};
    for(let i = 0; i < 10; i++){
      newNumbers[i] = Math.floor(Math.random() * 10 + 1);
    }
    setTimeout(() => {
      setDivNumbers(newNumbers);
      setIsAnimating(false);
    }, 2000);
  }

  const grid = [];

  for (let i = 0; i < 10; i++) {
    const key = i;
    grid.push(
      <div 
        key={key}
        style={{
          width: '50px',
          height: '50px',
          border: '1px solid black',
          backgroundColor: '#eee',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          opacity: isAnimating ? 0 : 1,
          transition: 'opacity 2s'
        }}
      >
        {divNumbers[key]}
      </div>
    );
  }

  return (
    <div className="grid" style={{userSelect: 'none'}}>
      {grid}
      <button onClick={handleClick} style={{marginTop: '20px'}}>Losuj liczby</button>
    </div>
  );
};

export default Siatka;
