import React, { useState, useEffect } from 'react';
import './App.css';

const Div = ({ setPool, pool }) => {
  const [number, setNumber] = useState(null);
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    if (isAnimating && pool.length > 0) {
      const interval = setInterval(() => {
        const randomNumberIndex = Math.floor(Math.random() * pool.length);
        setNumber(pool[randomNumberIndex]);
        setPool(pool.filter((_, index) => index !== randomNumberIndex));
      }, 100);

      setTimeout(() => {
        clearInterval(interval);
        setIsAnimating(false);
      }, 3000);
    }
  }, [isAnimating, pool, setPool]);

  const handleClick = () => {
    setIsAnimating(true);
  }

  return (
    <div 
      onClick={handleClick}
      style={{
        width: '50px',
        height: '50px',
        border: '1px solid black',
        backgroundColor: '#eee',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      {number}
    </div>
  );
}

const Siatka = () => {
  const [pool, setPool] = useState(Array.from({length: 50}, (_, i) => i + 1));
  const grid = [];

  for (let i = 0; i < 6; i++) {
    grid.push(
      <Div
        key={i}
        pool={pool}
        setPool={setPool}
      />
    );
  }

  return (
    <div className="grid" style={{userSelect: 'none', display: 'flex', gap: '10px'}}>
      {grid}
    </div>
  );
};

export default Siatka;
