import React, { useState, useEffect } from 'react';
import './App.css';

const Div = ({ startAnimation, onAnimationEnd }) => {
  const [number, setNumber] = useState(null);
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    if (startAnimation) {
      setIsAnimating(true);
    }
  }, [startAnimation]);

  useEffect(() => {
    if (isAnimating) {
      let interval = null;
      let finalNumber = null;

      interval = setInterval(() => {
        finalNumber = Math.floor(Math.random() * 49 + 1);
        setNumber(finalNumber);
      }, 100);

      setTimeout(() => {
        clearInterval(interval);
        setIsAnimating(false);
        onAnimationEnd(finalNumber);
      }, 2000);
    }
  }, [isAnimating, onAnimationEnd]);

  return (
    <div 
      style={{
        width: '50px',
        height: '50px',
        border: '1px solid black',
        backgroundColor: '#eee',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      {number}
    </div>
  );
}

const Siatka = () => {
  const [startAnimation, setStartAnimation] = useState(false);
  const [numbers, setNumbers] = useState([]);
  const [numberSets, setNumberSets] = useState([]);
  const grid = [];

  const handleAnimationEnd = (i, number) => {
    setNumbers((prev) => {
      const newNumbers = [...prev];
      newNumbers[i] = number;
      if (!newNumbers.includes(null)) { // jeżeli wszystkie liczby zostały wylosowane
        setNumberSets(prev => [...prev, newNumbers]);
      }
      return newNumbers;
    });
  }

  const handleClick = () => {
    setStartAnimation(prev => !prev);
    if (!startAnimation) {
      setNumbers(new Array(6).fill(null));
    }
  }

  for (let i = 0; i < 6; i++) {
    grid.push(
      <Div
        key={i}
        startAnimation={startAnimation}
        onAnimationEnd={(number) => handleAnimationEnd(i, number)}
      />
    );
  }

  return (
    <div className="grid" style={{userSelect: 'none', display: 'flex', gap: '10px', flexDirection: 'column'}}>
      <div style={{display: 'flex', gap: '10px'}}>
        {grid}
        <button onClick={handleClick} style={{marginTop: '20px'}}>Losuj liczby</button>
      </div>
      {numberSets.map((set, index) => (
        <div key={index} style={{border: '1px solid black', padding: '10px', margin: '10px 0', borderRadius: '5px'}}>
          {set.map((number, index) => <span key={index}>{number} </span>)}
        </div>
      ))}
    </div>
  );
};

export default Siatka;
