import React, { useState, useEffect } from 'react';
import './App.css';

const Siatka = () => {
  const [divNumbers, setDivNumbers] = useState({});
  const [lastNumber, setLastNumber] = useState(null);

  useEffect(() => {
    const duplicates = Object.entries(divNumbers).filter(([key, number]) => number === lastNumber);
    if (duplicates.length > 1) {
      setDivNumbers(prevNumbers => {
        const newNumbers = {...prevNumbers};
        duplicates.forEach(([key]) => {
          delete newNumbers[key];
        });
        return newNumbers;
      });
    }
  }, [divNumbers, lastNumber]);

  const handleClick = key => {
    const randomNumber = Math.floor(Math.random() * 10) + 1;
    setDivNumbers(prevState => ({
      ...prevState,
      [key]: randomNumber
    }));
    setLastNumber(randomNumber);
  };

  const grid = [];

  for (let i = 0; i < 10; i++) {
    for (let j = 0; j < 10; j++) {
      const key = `${i}-${j}`;
      grid.push(
        <div 
          key={key}
          onClick={() => handleClick(key)}
          style={{
            width: '50px',
            height: '50px',
            border: '1px solid black',
            backgroundColor: '#eee',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            opacity: divNumbers[key] ? '1' : '0',
            transition: 'opacity 0.3s ease'
          }}
        >
          {divNumbers[key]}
        </div>
      );
    }
  }

  return (
    <div className="grid" style={{userSelect: 'none'}}>
      {grid}
    </div>
  );
};

export default Siatka;
