import React, { useState } from 'react';
import './App.css';

const Siatka = () => {
  const [selectedDiv, setSelectedDiv] = useState(null);
  const grid = [];

  for (let i = 0; i < 10; i++) {
    for (let j = 0; j < 10; j++) {
      const key = `${i}-${j}`;
      grid.push(
        <div 
          key={key}
          style={{
            width: '50px',
            height: '50px',
            border: '1px solid black',
            backgroundColor: '#eee',
            boxShadow: selectedDiv === key ? '0 0 10px rgba(0,0,0,0.5)' : '',
            transition: 'boxShadow 0.3s ease'
          }}
          onClick={() => setSelectedDiv(key)}
        ></div>
      );
    }
  }

  return (
    <div className="grid">
      {grid}
    </div>
  );
};

export default Siatka;
