import React, { useState, useEffect } from 'react';
import './App.css';

const Div = ({ startAnimation, setPool, pool }) => {
  const [number, setNumber] = useState(null);
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    if (startAnimation && pool.length > 0) {
      setIsAnimating(true);
      const randomNumberIndex = Math.floor(Math.random() * pool.length);
      setNumber(pool[randomNumberIndex]);
      setPool(pool.filter((_, index) => index !== randomNumberIndex));
    }
  }, [startAnimation, pool, setPool]);

  useEffect(() => {
    if (isAnimating) {
      setTimeout(() => {
        setIsAnimating(false);
      }, 3000);
    }
  }, [isAnimating]);

  return (
    <div 
      style={{
        width: '50px',
        height: '50px',
        border: '1px solid black',
        backgroundColor: '#eee',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      {number}
    </div>
  );
}

const Siatka = () => {
  const [startAnimation, setStartAnimation] = useState(0);
  const [pool, setPool] = useState(Array.from({length: 50}, (_, i) => i + 1));
  const grid = [];

  const handleClick = () => {
    setStartAnimation(startAnimation + 1);
  }

  for (let i = 0; i < 6; i++) {
    if(i < startAnimation){
      grid.push(
        <Div
          key={i}
          startAnimation={true}
          pool={pool}
          setPool={setPool}
        />
      );
    } else {
      grid.push(
        <Div
          key={i}
          startAnimation={false}
          pool={pool}
          setPool={setPool}
        />
      );
    }
  }

  return (
    <div className="grid" style={{userSelect: 'none', display: 'flex', gap: '10px'}}>
      {grid}
      <button onClick={handleClick} style={{marginTop: '20px'}}>Losuj liczby</button>
    </div>
  );
};

export default Siatka;
