import React, { useState, useEffect } from 'react';

function Losowanie2() {
  const [numbers1, setNumbers1] = useState(0);
  const [numbers2, setNumbers2] = useState(0);
  const [numbers3, setNumbers3] = useState(0);
  const [spin, setSpin] = useState(false);
  
  const spinNumbers = () => {
    setNumbers1(Math.floor(Math.random() * 10));
    setNumbers2(Math.floor(Math.random() * 10));
    setNumbers3(Math.floor(Math.random() * 10));
  };

  useEffect(() => {
    let spinInterval;
    if (spin) {
      spinInterval = setInterval(() => {
        spinNumbers();
      }, 1000);
    } else if (!spin && spinInterval) {
      clearInterval(spinInterval);
    }
    return () => {
      clearInterval(spinInterval);
    };
  }, [spin]);

  const handleSpinClick = () => {
    setSpin(!spin);
  };

  return (
    <div className='slot-machine'>
  <button onClick={handleSpinClick}>{spin ? 'STOP' : 'SPIN'}</button>
  <div className='number-display'>
    {numbers.map((number, index) => (
      <div key={index} className='number'>
        <div className={`number-inner ${spin ? 'spin' : ''}`}>
          {number}
        </div>
      </div>
    ))}
  </div>
</div>
  );
}

export default Losowanie2;
