import React, { useState } from 'react';
import './App.css';

const Siatka = () => {
  const [divNumbers, setDivNumbers] = useState({});

  const handleClick = key => {
    setDivNumbers(prevState => ({
      ...prevState,
      [key]: Math.floor(Math.random() * 10)
    }));
  };

  const grid = [];

  for (let i = 0; i < 10; i++) {
    for (let j = 0; j < 10; j++) {
      const key = `${i}-${j}`;
      grid.push(
        <div 
          key={key}
          onClick={() => handleClick(key)}
          style={{
            width: '50px',
            height: '50px',
            border: '1px solid black',
            backgroundColor: '#eee',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center'
          }}
        >
          {divNumbers[key]}
        </div>
      );
    }
  }

  return (
    <div className="grid" style={{userSelect: 'none'}}>
      {grid}
    </div>
  );
};

export default Siatka;
