import React, { useState, useEffect, useRef } from 'react';

const speed = 180;

function Losowanie2() {
  const [numbers, setNumbers] = useState([]);
  const [isRunning, setIsRunning] = useState(false); 
  const intervalId = useRef(null); 
  const [moveUp, setMoveUp] = useState(false); 
  const [animating, setAnimating] = useState(false);

  const generateRandomNumber = () => {
    const randomNumber = Math.floor(Math.random() * 20) + 1;
    if(numbers.includes(randomNumber)) {
      return generateRandomNumber();
    }

    setNumbers(prevNumbers => {
      if (prevNumbers.length >= 5) {
        setMoveUp(true);
        setAnimating(true);
        return [...prevNumbers.slice(1), randomNumber];
      } 

      return [...prevNumbers, randomNumber];
    });
  }

  const handleButtonClick = () => {
    setIsRunning(!isRunning);
  }

  useEffect(() => {
    if (isRunning && !animating) {
      intervalId.current = setInterval(generateRandomNumber, speed);
    } else {
      // If the generating is running, stop the generating
      if (intervalId.current) clearInterval(intervalId.current);
    }
  }, [isRunning, animating]); 

  useEffect(() => {
    for(let i = 0; i < 5; i++) {
      generateRandomNumber();
    }
  }, []);

  const handleAnimationEnd = () => {
    setMoveUp(false);
    setAnimating(false);
  };

  return (
    <div className='zaLeb'>
      <button onClick={handleButtonClick}>{isRunning ? 'STOP' : 'START'}</button>
      {numbers.map((number, index) => (
         <div 
            key={index} 
            className={index === 2 ? 'middle-div' : (index === 1 ? 'gora-class' : (index === 3 ? 'dol-class' : '')) + (moveUp ? ' move-up' : '')}
            onAnimationEnd={handleAnimationEnd}
         >
            {number}
         </div>
      ))}
    </div>
  );
}

export default Losowanie2;
