import './App.css';
import React, { useState, useEffect } from "react";
import MainWindow from './MainWindow/MainWindow';
import Logowanie from './logowanie/logowanie';

function App() {
  const [loggedIn, setLoggedIn] = useState(JSON.parse(localStorage.getItem("loggedIn")) || false);
  const [serverMessage, setServerMessage] = useState(null);

  useEffect(() => {
    if (localStorage.getItem("loggedIn")) {
      setLoggedIn(true);
    }
  }, []);

  handleLogin = (message) => {
    this.setState({ loggedIn: true, serverMessage: message });
    localStorage.setItem("loggedIn", true);
  };

  const handleLogout = () => {
    setLoggedIn(false);
    localStorage.removeItem("loggedIn");
    setServerMessage(null);
  };

  if (loggedIn) {
    return <MainWindow handleLogout={handleLogout} serverMessage={serverMessage}/>
  } else {
    return <Logowanie handleLogin={handleLogin} />
  }
}

export default App;
