import React, { useState, useEffect } from 'react';

function Losowanie2() {
  const [numbers, setNumbers] = useState([0, 0, 0]);
  const [spin, setSpin] = useState(false);
  const [intervalId, setIntervalId] = useState(null);

  const generateNumber = (index) => {
    setNumbers(prevNumbers => {
      const newNumbers = [...prevNumbers];
      newNumbers[index] = Math.floor(Math.random() * 10);
      return newNumbers;
    });
  }

  useEffect(() => {
    if (spin) {
      const id = setInterval(() => {
        for (let i = 0; i < 3; i++) {
          generateNumber(i);
        }
      }, 1000); // Change numbers every second
      setIntervalId(id);
    } else {
      clearInterval(intervalId);
    }

    return () => {
      clearInterval(intervalId); // Cleanup interval on unmount
    };
  }, [spin]);

  const handleSpinClick = () => {
    setSpin(!spin);
  };

  return (
    <div className='slot-machine'>
      <button onClick={handleSpinClick}>{spin ? 'STOP' : 'SPIN'}</button>
      <div className='number-display'>
        {numbers.map((number, index) => (
          <div key={index} className='number'>
            <div className={`number-inner ${spin ? 'spin' : ''}`}>
              {number}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Losowanie2;
