import React, { useState, useEffect, useRef } from 'react';
import './App.css';

const Div = ({ startAnimation, number }) => {
  const [displayedNumber, setDisplayedNumber] = useState(null);
  const [isAnimating, setIsAnimating] = useState(false);
  const startAnimationRef = useRef(startAnimation);
  startAnimationRef.current = startAnimation;

  useEffect(() => {
    if (startAnimation) {
      setIsAnimating(true);
    }
  }, [startAnimation]);

  useEffect(() => {
    if (isAnimating) {
      const interval = setInterval(() => {
        setDisplayedNumber(Math.floor(Math.random() * 49 + 1));
      }, 100);

      setTimeout(() => {
        clearInterval(interval);
        setIsAnimating(false);
        setDisplayedNumber(startAnimationRef.current ? null : number);
      }, 2000);
    }
  }, [isAnimating, number]);

  return (
    <div 
      style={{
        width: '50px',
        height: '50px',
        border: '1px solid black',
        backgroundColor: '#eee',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      {displayedNumber}
    </div>
  );
}

const Siatka = () => {
  const [startAnimation, setStartAnimation] = useState(false);
  const [numbers, setNumbers] = useState([]);
  const grid = [];

  const handleClick = () => {
    setStartAnimation(prev => !prev);
    const nums = new Set();
    while(nums.size < 6) {
      nums.add(Math.floor(Math.random() * 49 + 1));
    }
    setNumbers([...nums]);
  }

  for (let i = 0; i < 6; i++) {
    grid.push(
      <Div
        key={i}
        startAnimation={startAnimation}
        number={numbers[i]}
      />
    );
  }

  return (
    <div className="grid" style={{userSelect: 'none', display: 'flex', gap: '10px'}}>
      {grid}
      <button onClick={handleClick} style={{marginTop: '20px'}}>Losuj liczby</button>
    </div>
  );
};

export default Siatka;
