import React, { useState, useRef } from 'react';

function DodajUzytkownika() {
  const [formState, setFormState] = useState({
    imie: "",
    nazwisko: "",
    dataUrodzenia: "",
    pesel: "",
    adresZamieszkania: "",
    adresKorespondencyjny: "",
    password: "",
    confirmPassword: ""
  });

  const [isCopied, setIsCopied] = useState(false);
  const passwordRef = useRef();

  const generatePassword = () => {
    const length = 20;
    const charset = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*";
    let retVal = "";
    for (let i = 0, n = charset.length; i < length; ++i) {
      retVal += charset.charAt(Math.floor(Math.random() * n));
    }
    setFormState({ ...formState, password: retVal, confirmPassword: retVal });
    setIsCopied(false); 
  };

  const handleChange = (e) => {
    setFormState({ ...formState, [e.target.name]: e.target.value });
    if (e.target.name === 'password' || e.target.name === 'confirmPassword') {
      setIsCopied(false); // Odblokuj przycisk po wprowadzeniu nowego hasła
    }
  };

  const handleCopy = () => {
    passwordRef.current.select();
    document.execCommand("copy");
    setIsCopied(true);
  };

  const handleSubmit = () => {
    // Tutaj musisz wysłać formState, który zawiera wartości wszystkich pól
    fetch('/adduser', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formState),
    })
    .then(response => response.json())
    .then(data => console.log(data))
    .catch((error) => {
      console.error('Error:', error);
    });
  };

  return (
    <div>
      <div className='dodajUzytkownika'>
        <form onSubmit={handleSubmit}>
          <table>
            <tr>
              <td>Imię</td>
              <td><input type='text' value={formState.imie} onChange={handleChange} name='imie' /></td>
            </tr>
            <tr>
              <td>Nazwisko</td>
              <td><input type='text' value={formState.nazwisko} onChange={handleChange} name='nazwisko' /></td>
            </tr>
            <tr>
              <td>Data urodzenia</td>
              <td><input type='text' value={formState.dataUrodzenia} onChange={handleChange} name='dataUrodzenia' /></td>
            </tr>
            <tr>
              <td>PESEL</td>
              <td><input type='text' value={formState.pesel} onChange={handleChange} name='pesel' /></td>
            </tr>
            <tr>
              <td>Adres zamieszkania</td>
              <td><input type='text' value={formState.adresZamieszkania} onChange={handleChange} name='adresZamieszkania' /></td>
            </tr>
            <tr>
              <td>Adres korespondencyjny</td>
              <td><input type='text' value={formState.adresKorespondencyjny} onChange={handleChange} name='adresKorespondencyjny' /></td>
            </tr>
            <tr>
              <td>Hasło</td>
              <td>
                <input value={formState.password} type="password" ref={passwordRef} onChange={handleChange} name="password" />
                {(formState.password && formState.confirmPassword && formState.password === formState.confirmPassword) &&
                  <div className='btn-kopiuj'>
                    <button type="button" onClick={handleCopy} disabled={isCopied}>Kopiuj</button>
                  </div>
                }
              </td>
            </tr>
            <tr>
              <td>Powtórz hasło</td>
              <td><input value={formState.confirmPassword} type="password" onChange={handleChange} name="confirmPassword" /></td>
            </tr>
          </table>
          <div className='generujHasloDiv'>
            <div className='odw'>
              <button type="button" onClick={generatePassword}>Generuj hasło</button>
            </div>
          </div>
          <div>
            <button type="submit">Dodaj</button>
          </div>
        </form>
      </div>
    </div>
  );

};


export default DodajUzytkownika;
