import React, { useState, useEffect } from 'react';
import './App.css';

const Div = ({ triggerAnimation, number }) => {
  const [displayedNumber, setDisplayedNumber] = useState(null);

  useEffect(() => {
    if (triggerAnimation) {
      const interval = setInterval(() => {
        setDisplayedNumber(Math.floor(Math.random() * 49 + 1));
      }, 100);

      setTimeout(() => {
        clearInterval(interval);
        setDisplayedNumber(number);
      }, 2000);
    }
  }, [triggerAnimation, number]);

  return (
    <div 
      style={{
        width: '50px',
        height: '50px',
        border: '1px solid black',
        backgroundColor: '#eee',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      {displayedNumber}
    </div>
  );
}

const Siatka = () => {
  const [numbers, setNumbers] = useState([]);
  const [numberSets, setNumberSets] = useState([]);
  const [storedSets, setStoredSets] = useState(JSON.parse(localStorage.getItem('numberSets')) || []);
  const [triggerAnimation, setTriggerAnimation] = useState(false);
  const grid = [];

  const handleClick = () => {
    const newNumbers = [];
    const availableNumbers = Array.from({length: 49}, (_, i) => i + 1);

    for (let i = 0; i < 6; i++) {
      const randomIndex = Math.floor(Math.random() * availableNumbers.length);
      newNumbers.push(availableNumbers[randomIndex]);
      availableNumbers.splice(randomIndex, 1);
    }

    setNumbers(newNumbers);
    setTriggerAnimation(true);
  }

  useEffect(() => {
    if (triggerAnimation) {
      setTimeout(() => {
        const newSets = [numbers, ...numberSets.slice(0, 4)];
        setNumberSets(newSets);
        setStoredSets(newSets);
        localStorage.setItem('numberSets', JSON.stringify(newSets));
        setTriggerAnimation(false);
      }, 2000);
    }
  }, [triggerAnimation, numbers, numberSets]);

  for (let i = 0; i < 6; i++) {
    grid.push(
      <Div
        key={i}
        number={numbers[i]}
        triggerAnimation={triggerAnimation}
      />
    );
  }

  return (
    <div className="grid" style={{userSelect: 'none', display: 'flex', gap: '10px', flexDirection: 'column'}}>
      <div style={{display: 'flex', gap: '10px'}}>
        {grid}
        <button onClick={handleClick} style={{marginTop: '20px'}}>Losuj liczby</button>
      </div>
      <div style={{display: 'flex', flexDirection: 'row'}}>
        <div style={{display: 'flex', flexDirection: 'column'}}>
          {numberSets.map((set, index) => (
            <div key={index} style={{backgroundColor: 'lightgreen', color: 'white', border: '1px solid black', padding: '10px', margin: '10px 0', borderRadius: '5px'}}>
              {set.map((number, index) => <span key={index}>{number} </span>)}
            </div>
          ))}
        </div>
        <div style={{display: 'flex', flexDirection: 'column'}}>
          {storedSets.map((set, index) => (
            <div key={index} style={{backgroundColor: 'lightgreen', color: 'white', border: '1px solid black', padding: '10px', margin: '10px 0', borderRadius: '5px'}}>
              {set.map((number, index) => <span key={index}>{number} </span>)}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Siatka;
