import React, { useState, useEffect } from 'react';
import './App.css';

const generateShuffledNumbers = (min, max) => {
  let numbers = Array.from({length: max - min + 1}, (_, i) => i + min);
  for(let i = numbers.length - 1; i > 0; i--){
    const j = Math.floor(Math.random() * (i + 1));
    [numbers[i], numbers[j]] = [numbers[j], numbers[i]];
  }
  return numbers;
};

const Div = ({ startAnimation, number, animationDuration, minNumber, maxNumber, handleSliderChange, displayedNumbersProp }) => {

  return (
    <div style={{ margin: '20px' }}>
      <div>Speed Break</div>
      <input 
        className='rangeSpeed'
        type="range" 
        min="0.1" 
        max="0.4" 
        step="0.1" 
        value={animationDuration} 
        onChange={handleSliderChange} 
        orient="vertical"
        style={{writingMode: 'bt-lr'}}
      />
      <div style={{ width: '50px', height: '50px', border: '1px solid black', backgroundColor: '#eee', display: 'flex', justifyContent: 'center', alignItems: 'center', overflow: 'hidden' }}>
        <div 
          className={startAnimation ? 'number-spin' : ''} 
          style={{ position: 'absolute', animationDuration: `${animationDuration}s` }}
        >
          {displayedNumbersProp.map((num, index) => (
            <div key={index} style={{ marginBottom: '2px' }}>{num}</div>
          ))}
        </div>
      </div>
    </div>
  );
};

const Losowanie = () => {
  const [startAnimation, setStartAnimation] = useState(false);
  const [number, setNumber] = useState();
  const [animationDuration, setAnimationDuration] = useState(0.1);
  const [minNumber, setMinNumber] = useState(1);
  const [maxNumber, setMaxNumber] = useState(49);
  const [speedBreak, setSpeedBreak] = useState(3000);

  const [displayedNumbers, setDisplayedNumbers] = useState([]);

  useEffect(() => {
    setDisplayedNumbers(generateShuffledNumbers(minNumber, maxNumber));
  }, []);

  const handleClick = () => {
    const num = Math.floor(Math.random() * (maxNumber - minNumber + 1) + minNumber);
    setNumber(num);
    setStartAnimation(true);
  }

  useEffect(() => {
    let timeout = setTimeout(() => {
      setStartAnimation(false);
    }, animationDuration * speedBreak);

    return () => clearTimeout(timeout);
  }, [number, animationDuration, speedBreak]); 
  
  const handleSpeedBreakChange = (e) => {
    setSpeedBreak(Number(e.target.value)); 
  };

  const handleSliderChange = (e) => {
    setAnimationDuration(Number(e.target.value));
  }

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <h1>Losowanie Lotto</h1>
      <div className="siemaE" style={{ display: 'flex',  }}>
        <Div
          startAnimation={startAnimation}
          number={number}
          animationDuration={animationDuration}
          minNumber={minNumber}
          maxNumber={maxNumber}
          handleSliderChange={handleSliderChange}
          displayedNumbersProp={displayedNumbers}
        />
      </div>
      <input 
        type="range" 
        min="1000" 
        max="20000" 
        step="1000" 
        value={speedBreak} 
        onChange={handleSpeedBreakChange} 
      />
      <button onClick={handleClick}>Losuj liczby</button>
    </div>
  );
};

export default Losowanie;
