import React, { useState, useEffect } from 'react';
import './App.css';

const Div = ({ animate, setPool, pool }) => {
  const [number, setNumber] = useState(null);

  useEffect(() => {
    if (animate && pool.length > 0) {
      const randomNumberIndex = Math.floor(Math.random() * pool.length);
      setNumber(pool[randomNumberIndex]);
      setPool(pool.filter((_, index) => index !== randomNumberIndex));
    }
  }, [animate, pool, setPool]);

  return (
    <div 
      style={{
        width: '50px',
        height: '50px',
        border: '1px solid black',
        backgroundColor: '#eee',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      {number}
    </div>
  );
}

const Siatka = () => {
  const [pool, setPool] = useState(Array.from({length: 50}, (_, i) => i + 1));
  const [isAnimating, setIsAnimating] = useState(Array(6).fill(false));
  const grid = [];

  const handleClick = () => {
    for (let i = 0; i < 6; i++) {
      setTimeout(() => {
        setIsAnimating(prev => {
          const copy = [...prev];
          copy[i] = true;
          return copy;
        });
      }, i * 3000);
    }
  }

  for (let i = 0; i < 6; i++) {
    grid.push(
      <Div
        key={i}
        animate={isAnimating[i]}
        pool={pool}
        setPool={setPool}
      />
    );
  }

  return (
    <div className="grid" style={{userSelect: 'none', display: 'flex', gap: '10px'}}>
      {grid}
      <button onClick={handleClick} style={{marginTop: '20px'}}>Losuj liczby</button>
    </div>
  );
};

export default Siatka;
