import React, { useState, useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import './App.css';
const [animationDuration, setAnimationDuration] = useState(Number(localStorage.getItem('animationDuration')) || 0.1);
const [speedBreak, setSpeedBreak] = useState(Number(localStorage.getItem('speedBreak')) || 3000);


const handleSpeedBreakChange = (e) => {
  const newSpeedBreak = Number(e.target.value);
  setSpeedBreak(newSpeedBreak); 

  // Zapisz nową wartość do Local Storage
  localStorage.setItem('speedBreak', newSpeedBreak);
};

const handleSliderChange = (e) => {
  const newAnimationDuration = Number(e.target.value);
  setAnimationDuration(newAnimationDuration);

  // Zapisz nową wartość do Local Storage
  localStorage.setItem('animationDuration', newAnimationDuration);
}

const generateShuffledNumbers = (min, max) => {
  let numbers = Array.from({length: max - min + 1}, (_, i) => i + min);
  for(let i = numbers.length - 1; i > 0; i--){
    const j = Math.floor(Math.random() * (i + 1));
    [numbers[i], numbers[j]] = [numbers[j], numbers[i]];
  }
  return numbers;
};

const Div = ({ startAnimation, number, animationDuration, minNumber, maxNumber, handleSliderChange, displayedNumbersProp, speedBreak, setStartAnimation }) => {
  const animRef = useRef(null);
  let animation;

  useEffect(() => {
    if (startAnimation) {
      const totalHeight = 52 * maxNumber * 5;
      animation = gsap.to(animRef.current, {
        y: `-=${totalHeight}px`,
        duration: speedBreak / 1000,
        repeat: 0,
        onComplete: () => {
          setStartAnimation(false);
          const offset = displayedNumbersProp.indexOf(number) * 52;
          gsap.set(animRef.current, { y: `+=${offset}px` });
        },
      });
    }
  }, [startAnimation, speedBreak, maxNumber, displayedNumbersProp, number, setStartAnimation]);

  return (
    <div style={{ margin: '20px' }}>
      <div>Speed Break</div>
      <input 
        className='rangeSpeed'
        type="range" 
        min="1" 
        max="10" 
        step="1" 
        value={animationDuration} 
        onChange={handleSliderChange} 
        orient="vertical"
        style={{writingMode: 'bt-lr'}}
      />
      <div style={{ width: '50px', height: '50px', border: '1px solid black', backgroundColor: '#eee', display: 'flex', justifyContent: 'center', alignItems: 'center', overflow: 'hidden' }}>
        <div 
          ref={animRef}
          style={{ position: 'relative'}}
        >
          {displayedNumbersProp.map((num, index) => (
            <div key={index} style={{ marginBottom: '2px' }}>{num}</div>
          ))}
        </div>
      </div>
    </div>
  );
};



const Losowanie = () => {
  const [startAnimation, setStartAnimation] = useState(false);
  const [number, setNumber] = useState();
  const [animationDuration, setAnimationDuration] = useState(0.1);
  const [minNumber, setMinNumber] = useState(1);
  const [maxNumber, setMaxNumber] = useState(49);
  const [speedBreak, setSpeedBreak] = useState(3000);

  

  const [displayedNumbers, setDisplayedNumbers] = useState(generateShuffledNumbers(minNumber, maxNumber));

  useEffect(() => {
    setDisplayedNumbers(generateShuffledNumbers(minNumber, maxNumber));
  }, [minNumber, maxNumber]);

  const handleClick = () => {
    const index = Math.floor(Math.random() * displayedNumbers.length);
    const num = displayedNumbers[index];
    console.log(num);
    setNumber(num);
    setStartAnimation(true);
  }

  useEffect(() => {
    let timeout = setTimeout(() => {
      setStartAnimation(false);
    }, animationDuration * speedBreak);

    return () => clearTimeout(timeout);
  }, [number, animationDuration, speedBreak]); 

  const handleSpeedBreakChange = (e) => {
    setSpeedBreak(Number(e.target.value)); 
  };

  const handleSliderChange = (e) => {
    setAnimationDuration(Number(e.target.value));
  }

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <h1>Losowanie Lotto</h1>
      <div className="siemaE" style={{ display: 'flex',  }}>
        <Div
          startAnimation={startAnimation}
          number={number}
          animationDuration={animationDuration}
          minNumber={minNumber}
          maxNumber={maxNumber}
          handleSliderChange={handleSliderChange}
          displayedNumbersProp={displayedNumbers}
          speedBreak={speedBreak}
          setStartAnimation={setStartAnimation}
        />
      </div>
      <input 
        type="range" 
        min="1000" 
        max="20000" 
        step="1000" 
        value={speedBreak} 
        onChange={handleSpeedBreakChange} 
      />
      <button disabled={startAnimation} onClick={handleClick}>Losuj liczby</button>
    </div>
  );
};

export default Losowanie;
