import React, { useState } from 'react';
const URL = 'http://localhost:3001';

function Uzytkownicy() {
  const [displayData, setDisplayData] = useState(false);
  const [username, setUsername] = useState("ADMIN11");
  const [password, setPassword] = useState("admino");
  const [token, setToken] = useState("");
  const [data, setData] = useState([]); // Dodano stan dla danych

  const handleClick = async e => {
    e.preventDefault();

    try {
      const response = await fetch(`${URL}/uzytkownicy`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password, token })
      });

      const data = await response.json();
      console.log(data);

      // Aktualizacja stanu za pomocą odpowiedzi z serwera
      setData(data);

      setDisplayData(true); // Wyświetlanie danych po pobraniu
    } catch (error) {
      console.log(`Niepoprawne dane logowania: ${error.message}`);
    }
  };

  return(
    <div>
      <button onClick={handleClick}>Szukaj</button>

      {displayData && (
        <div className="vh80 divWithScroll">
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Age</th>
                <th>Job</th>
                <th>OPT</th>
              </tr>
            </thead>
            <tbody>
              {data.map((person, index) => (
                <tr key={index}>
                  <td>{person.name}</td>
                  <td>{person.age}</td>
                  <td>{person.job}</td>
                  <td>
                    <button>Edytuj</button>
                    <button>Usuń</button>
                    <button>Zablokuj</button>
                    <select>
                      <option>Dodaj karte</option>
                      <option>RFID</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Uzytkownicy;
