import React, { useState } from 'react';
import './App.css';

const Siatka = () => {
  const [selectedDiv, setSelectedDiv] = useState(null);
  const grid = [];
  
  for (let i = 0; i < 10; i++) {
    for (let j = 0; j < 10; j++) {
      const key = `${i}-${j}`;
      grid.push(
        <div 
          key={key} 
          className={`grid-item ${selectedDiv === key ? 'selected' : ''}`} 
          onClick={() => setSelectedDiv(key)}
        ></div>
      );
    }
  }

  return (
    <div className="grid">
      {grid}
    </div>
  );
};

export default Siatka;
