import React, { useState, useEffect } from 'react';
import './App.css';

const Div = ({ onClick, isAnimating }) => {
  const [number, setNumber] = useState(null);

  useEffect(() => {
    if (isAnimating) {
      const interval = setInterval(() => {
        setNumber(Math.floor(Math.random() * 10 + 1));
      }, 100);

      setTimeout(() => {
        clearInterval(interval);
      }, 3000);
    }
  }, [isAnimating]);

  return (
    <div 
      onClick={onClick}
      style={{
        width: '50px',
        height: '50px',
        border: '1px solid black',
        backgroundColor: '#eee',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      {number}
    </div>
  );
}

const Siatka = () => {
  const [isAnimating, setIsAnimating] = useState(false);

  const handleClick = () => {
    setIsAnimating(true);
    setTimeout(() => {
      setIsAnimating(false);
    }, 3000);
  }

  const grid = [];

  for (let i = 0; i < 10; i++) {
    grid.push(
      <Div
        key={i}
        onClick={handleClick}
        isAnimating={isAnimating}
      />
    );
  }

  return (
    <div className="grid" style={{userSelect: 'none', display: 'flex', gap: '10px'}}>
      {grid}
      <button onClick={handleClick} style={{marginTop: '20px'}}>Losuj liczby</button>
    </div>
  );
};

export default Siatka;
