import React, { useState } from "react";

const Test2 = () => {
  const [divs, setDivs] = useState([1, 2, 3, 4, 5]);
  const [nextNum, setNextNum] = useState(6);
  const [inputValue, setInputValue] = useState("");

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleButtonClick = () => {
    const numDivsToRemove = parseInt(inputValue, 10);

    if (!isNaN(numDivsToRemove)) {
      setDivs(prevDivs => {
        let newDivs = [...prevDivs];
        newDivs = newDivs.slice(0, -numDivsToRemove); // usuwamy określoną liczbę divów
        newDivs.unshift(...Array(numDivsToRemove).fill(nextNum).map((_, i) => nextNum + i)); // dodajemy określoną liczbę divów na początek
        return newDivs;
      });

      setNextNum(prevNum => prevNum + numDivsToRemove); // zwiększamy liczbę dla następnych divów
    }
  };

  return (
    <div>
      <input type="number" value={inputValue} onChange={handleInputChange} />
      <button onClick={handleButtonClick}>Usunąć i dodać div</button>
      {divs.map((num, i) => (
        <div key={i} style={{ height: "50px", width: "50px", backgroundColor: "lightblue", margin: "5px" }}>
          {num}
        </div>
      ))}
    </div>
  );
};
export default Test2;
