import './App.css';
import React, { Component } from "react";
import MainWindow from './MainWindow/MainWindow';
import Logowanie from './logowanie/logowanie';

class App extends Component {

  // const [serverMessage, setServerMessage] = useState('');

  constructor() {
    super();
    this.state = {
      loggedIn: JSON.parse(localStorage.getItem("loggedIn")) || false,
      serverMessage: ''
    };
  }

  // state = {
  //   loggedIn: false,
  // };

  handleLogin = () => {
    // tu powinna być logika logowania, np. zapytanie do API
    this.setState({ loggedIn: true });
    localStorage.setItem("loggedIn", true);
  };

  handleLogout = () => {
    // tu powinna być logika wylogowywania, np. usunięcie tokena
    this.setState({ loggedIn: false });
    localStorage.removeItem("loggedIn");
  };

  render() {
    if (this.state.loggedIn) {
      return <MainWindow handleLogout={this.handleLogout} serverMessage={serverMessage}/>
    } else {
      return <Logowanie handleLogin={this.handleLogin} />
    }
  }
}




export default App;
