import React, { useState, useEffect } from 'react';

function Losowanie2() {
  const [numbers1, setNumbers1] = useState([0]);
  const [numbers2, setNumbers2] = useState([0]);
  const [numbers3, setNumbers3] = useState([0]);
  const [spin, setSpin] = useState(false);
  const [intervalId, setIntervalId] = useState(null);

  const generateNumber = (setNumbers) => {
    setNumbers(prevNumbers => {
      const newNumbers = [...prevNumbers];
      newNumbers[0] = Math.floor(Math.random() * 10);
      return newNumbers;
    });
  }

  useEffect(() => {
    if (spin) {
      const id = setInterval(() => {
        generateNumber(setNumbers1);
        generateNumber(setNumbers2);
        generateNumber(setNumbers3);
      }, 1000); // Change numbers every second
      setIntervalId(id);
    } else {
      clearInterval(intervalId);
    }

    return () => {
      clearInterval(intervalId); // Cleanup interval on unmount
    };
  }, [spin]);

  const handleSpinClick = () => {
    setSpin(!spin);
  };

  return (
    <div className='slot-machine'>
      <button onClick={handleSpinClick}>{spin ? 'STOP' : 'SPIN'}</button>
      <div className='number-display'>
        {[numbers1, numbers2, numbers3].map((numbers, idx) => (
          <div key={idx}>
            {numbers.map((number, index) => (
              <div key={index} className={`number ${spin ? 'spin' : ''}`}>
                <div className='number-inner'>
                  {number}
                </div>
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Losowanie2;
