import React, { useEffect, useState } from 'react';
import './App.css'; 

const Siatka = () => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  const divs = Array.from({ length: 100 }).map((_, i) => (
    <div 
      key={i} 
      className={`grid-item ${loading ? 'grid-item-loading' : ''}`} 
      style={{ 
        animationDelay: `${i * 0.05}s`
      }} 
    />
  ));

  return (
    <div className="grid">
      {divs}
    </div>
  );
};

export default Siatka;
