import React, { useState, useRef } from 'react';

function DodajUzytkownika() {
  const [formState, setFormState] = useState({
    imie: "",
    nazwisko: "",
    dataUrodzenia: "",
    pesel: "",
    adresZamieszkania: "",
    adresKorespondencyjny: "",
    password: "",
    confirmPassword: ""
  });

  const [isCopied, setIsCopied] = useState(false);
  const passwordRef = useRef();

  const generatePassword = () => {
    const length = 20;
    const charset = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*";
    let retVal = "";
    for (let i = 0, n = charset.length; i < length; ++i) {
      retVal += charset.charAt(Math.floor(Math.random() * n));
    }
    setFormState({ ...formState, password: retVal, confirmPassword: retVal });
    setIsCopied(false); 
  };












  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormState(prevState => {
      let newState = { ...prevState, [name]: value };

      if (name === 'pesel' && value.length >= 6) {
        const yearPrefix = value.substr(0, 2);
        const month = value.substr(2, 2);
        const day = value.substr(4, 2);

        const currentYear = new Date().getFullYear();
        let centuryPrefix = '19';

        // Jeśli rok wynikający z numeru PESEL jest późniejszy niż aktualny rok minus 100, dodaj 2000 zamiast 1900
        if (parseInt(yearPrefix, 10) > currentYear % 100 && `20${yearPrefix}` <= currentYear) {
          centuryPrefix = '20';
        }

        const formattedDate = `${centuryPrefix}${yearPrefix}-${month}-${day}`;
        newState = { ...newState, dataUrodzenia: formattedDate };
      }

      if (name === 'password' || name === 'confirmPassword') {
        setIsCopied(false); // Odblokuj przycisk po wprowadzeniu nowego hasła
      }

      return newState;
    });
  };













  const handleCopy = () => {
    passwordRef.current.select();
    document.execCommand("copy");
    setIsCopied(true);
  };

  const handleSubmit = () => {
    // Tutaj musisz wysłać formState, który zawiera wartości wszystkich pól
    fetch('/adduser', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formState),
    })
    .then(response => response.json())
    .then(data => console.log(data))
    .catch((error) => {
      console.error('Error:', error);
    });
  };

  return (
    <div>
      <div className='dodajUzytkownika'>
        <form onSubmit={handleSubmit}>
          <div className='underForm'>
          <div className='COL11'>
            <div>Imię</div>
            <div>Nazwisko</div>
            <div>PESEL</div>
            <div>Data urodzenia</div>
            <div>Adres zamieszkania</div>
            <div>Adres adres korespondencyjny</div>
            <div className='myContainer'>
              <div>Hasło</div>
              {(formState.password && formState.confirmPassword && formState.password === formState.confirmPassword) &&
                <div className='btn-kopiuj'>
                  <button type="button" onClick={handleCopy} disabled={isCopied}>Kopiuj</button>
                </div>
              }
            </div>
            <div className='haslo2'>Powtórz hasło</div>
          </div>

          <div className='COL22'>
            <input type='text' value={formState.imie} onChange={handleChange} name='imie' />
            <input type='text' value={formState.nazwisko} onChange={handleChange} name='nazwisko' />
            <input type='text' value={formState.pesel} onChange={handleChange} name='pesel' />
            <input type='date' value={formState.dataUrodzenia} onChange={handleChange} name='dataUrodzenia' />
            <input type='text' value={formState.adresZamieszkania} onChange={handleChange} name='adresZamieszkania' />
            <input type='text' value={formState.adresKorespondencyjny} onChange={handleChange} name='adresKorespondencyjny' />
            <input value={formState.password} type="password" ref={passwordRef} onChange={handleChange} name="password" />
            <input value={formState.confirmPassword} type="password" onChange={handleChange} name="confirmPassword" />
          </div>
          <div className='generujHasloDiv'>
            <div className='odw'>
              <button type="button" onClick={generatePassword}>Generuj hasło</button>
            </div>
          </div>
          </div>
          <div>
            <button type="submit">Dodaj</button>
          </div>
        </form>
      </div>
    </div>
  );
};


export default DodajUzytkownika;
