import React, { useState, useRef } from 'react';

function DodajUzytkownika() {
  const [password, setPassword] = useState("");
  const [inputType, setInputType] = useState("password");
  const [isGenerated, setIsGenerated] = useState(false); 
  const passwordRef = useRef(); // Utwórz referencję

  const generatePassword = () => {
    const length = 20;
    const charset = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*";
    let retVal = "";
    for (let i = 0, n = charset.length; i < length; ++i) {
      retVal += charset.charAt(Math.floor(Math.random() * n));
    }
    setPassword(retVal);
    setInputType("text");
    setIsGenerated(true);
  };

  const handleUserInput = (e) => {
    setPassword(e.target.value);
    if (!isGenerated) {
      setInputType("password");
    }
  };

  const handleFocus = () => {
    if(isGenerated) {
      setPassword("");
      setInputType("password");
      setIsGenerated(false);
    }
  }

  const handleCopy = () => {
    passwordRef.current.select(); // Wybierz tekst
    document.execCommand("copy"); // Skopiuj
  };

  return (
  <>
  <div className='dodajUzytkownika'>
    <div className='COL11'>
        <div>Imię</div>
        <div>Nazwisko</div>
        <div>Data urodzenia</div>
        <div>PESEL</div>
        <div>Adres zamieszkania</div>
        <div>Adres adres korespondencyjny</div>
        <div>Hasło do skopiowania</div>
        <div>Hasło</div>
        <div>Powtórz hasło</div>
      </div>

      <div className='COL22'>
        <input></input>
        <input></input>
        <input></input>
        <input></input>
        <input></input>
        <input></input>
        <input value={password} readOnly ref={passwordRef}></input> {/* Dodaj input do skopiowania hasła */}
        <button onClick={handleCopy}>Kopiuj</button> {/* Dodaj przycisk do kopiowania hasła */}
        <input type={inputType} value={password} onChange={handleUserInput} onFocus={handleFocus}></input>
        <input type="password"></input>

      </div>
      <div className='generujHasloDiv'>
        <button onClick={generatePassword}>Generuj hasło</button>
      </div>
    </div>
    <div>
      <button>Dodaj</button>
    </div>
  </>
  );
};

export default DodajUzytkownika;
