import React, { useState, useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import './App.css';

const generateShuffledNumbers = (min, max) => {
  let numbers = Array.from({length: max - min + 1}, (_, i) => i + min);
  for(let i = numbers.length - 1; i > 0; i--){
    const j = Math.floor(Math.random() * (i + 1));
    [numbers[i], numbers[j]] = [numbers[j], numbers[i]];
  }
  return numbers;
};

const Losowanie2 = () => {
  const [displayedNumbers, setDisplayedNumbers] = useState(generateShuffledNumbers(1, 49));
  const [currentNumberIndex, setCurrentNumberIndex] = useState(0);
  const [numbersToDisplay, setNumbersToDisplay] = useState(10); // ile numerów jest jednocześnie widocznych
  const [animationSpeed, setAnimationSpeed] = useState(1000); // szybkość animacji w milisekundach
  const [isRunning, setIsRunning] = useState(false); // czy animacja jest uruchomiona

  useEffect(() => {
    let intervalId;

    if (isRunning) {
      intervalId = setInterval(() => {
        setCurrentNumberIndex((currentNumberIndex + 1) % displayedNumbers.length);
      }, animationSpeed);
    }

    return () => clearInterval(intervalId);
  }, [currentNumberIndex, isRunning, animationSpeed, displayedNumbers]);

  const startAnimation = () => {
    setIsRunning(true);
  };

  const stopAnimation = () => {
    setIsRunning(false);
  };

  return (
    <div style={{ margin: '20px' }}>
      <div>Speed Break</div>
      <input 
        className='rangeSpeed'
        type="range" 
        min="1" 
        max="5" 
        step="0.1" 
        value={animationSpeed / 1000} 
        onChange={(e) => setAnimationSpeed(e.target.value * 1000)} 
        orient="vertical"
        style={{writingMode: 'bt-lr'}}
      />
      <div style={{ width: '50px', height: '60px', border: '1px solid black', backgroundColor: '#eee', display: 'flex', flexDirection: 'column', justifyContent: 'flex-start', alignItems: 'center', overflow: 'hidden' }}>
      <div 
        style={{ position: 'relative', top: -currentNumberIndex * 30 + 15 }} // aktualizacja tu
      >
        {displayedNumbers.map((number, index) => <div key={index} style={{ height: '23px' }}>{number}</div>)}
      </div>

      </div>
      <button onClick={startAnimation}>Start</button>
      <button onClick={stopAnimation}>Stop</button>
    </div>
  );
};

export default Losowanie2;
