import React, { useState, useEffect} from "react";
import logo from '../MainWindow/img/logo.png';
import URL from '../config';
function Logowanie({ handleLogin }) {
  // Tworzenie stanu dla formularza
  const [username, setUsername] = useState("ADMIN11");
  const [password, setPassword] = useState("admino");
  const [token, setToken] = useState("");

  useEffect(() => {
    setToken(generateToken());
  }, []);

  const generateToken = () => {
    return '_' + Math.random().toString(36).substr(2, 9);
  };

  // Obsługa logowania
  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch(`${URL}/logowanie`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ username, password, token })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Coś poszło nie tak');
      }

      // Zapisz token do stanu (lub do localStorage dla trwałości)
      setToken(data.token);
      handleLogin();
      alert("Zalogowano pomyślnie!");

    } catch (error) {
      alert(`Niepoprawne dane logowania: ${error.message}`);
    }
  };


  return (
    <div id="LOGOWANIE">
      <div class="LG">
        <div class="logos">
          <img src={logo} alt="" class="logos-SWG" />
        </div>

        <div id="logowanie-s">
          <form onSubmit={handleSubmit}>
          <input
                type="hidden"
                id="token"
                autocomplete="off"
                name="token"
                placeholder="Token"
                value={token}
                class="w100"
              />

            <div id="name">
              <i class="material-icons prefix cwhite">apps</i>
              <input
                type="text"
                id="first"
                autocomplete="off"
                autofocus="On"
                name="user"
                placeholder="Login"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                class="w100"
                required
              />
            </div>

            <div id="pass">
              <i class="material-icons prefix cwhite">lock</i>
              <input
                type="password"
                id="second"
                autocomplete="off"
                name="pass"
                placeholder="Hasło"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                class="w100"
                required
              />
            </div>

            <div id="btn-log-in">
              <div class="wrapper">
                <div class="box">
                  <button type="submit" class="log-in" id="sub">
                    Sign In
                  </button>
                </div>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Logowanie;
