import React, { useState, useEffect } from 'react';
const URL = 'http://localhost:3001';

function Uzytkownicy() {
  const [displayData, setDisplayData] = useState(false);
  const [username, setUsername] = useState("ADMIN11");
  const [password, setPassword] = useState("admino");
  const [token, setToken] = useState("");
  const [data, setData] = useState([]); // Stan dla danych
  const [page, setPage] = useState(1); // Stan dla strony

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch(`${URL}/uzytkownicy?page=${page}`, {
          method: 'GET',
          headers: { 'Content-Type': 'application/json' },
        });

        const data = await response.json();

        // Aktualizacja stanu za pomocą odpowiedzi z serwera
        setData(data);

        setDisplayData(true); // Wyświetlanie danych po pobraniu
      } catch (error) {
        console.log(`Niepoprawne dane logowania: ${error.message}`);
      }
    };

    fetchUsers();
