import React, { useState } from 'react';
const URL = 'http://localhost:3001';

function SzukajUzytkownika() {

  const [data, setData] = useState([]); 
  const [page, setPage] = useState(1);

  const fetchUsers = async (pageNumber) => {
    try {
      const response = await fetch(`${URL}/uzytkownicy?page=${pageNumber}`, {
        method: 'post',
        headers: { 'Content-Type': 'application/json' },
      });

      const data = await response.json();
      console.log(data);

      setData(data);
      return;
    } catch (error) {
      console.log(`Niepoprawne dane logowania: ${error.message}`);
    }
  };

  const nextPage = () => setPage(page + 1);
  const prevPage = () => setPage(page > 1 ? page - 1 : page);

  return (
        <>
          <div className="vh80 divWithScroll">
            <div className='usersMenu'>
              <button onClick={() => { prevPage(); fetchUsers(page); }}> &lt;</button>
              <button onClick={() => { nextPage(); fetchUsers(page); }}> &gt;</button>
              <select>
                <option>25</option>
                <option>50</option>
                <option>100</option>
                <option>200</option>
                <option>500</option>
                <option>1000</option>
                <option>5000</option>
              </select>
            </div>

            <table>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Name</th>
                  <th>Age</th>
                  <th>OPT</th>
                </tr>
              </thead>
              <tbody>
                {data.map((person, index) => (
                  <tr key={index}>
                    <td>{person.id}</td>
                    <td>{person.user}</td>
                    <td>{person.age}</td>

                    <td>
                      <button>Edytuj</button>
                      <button>Usu≈Ñ</button>
                      <button>Zablokuj</button>
                      <select>
                        <option>Dodaj karte</option>
                        <option>RFID</option>
                      </select>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
  );
};

export default SzukajUzytkownika;
