import { useState } from 'react';
import { URL } from 'http://localhost:3000';
// import { useNavigate } from 'react-router-dom';

const Login = () => {

    const [form, setForm] = useState({
        email: '',
        temat: 'Mam pytanie',
        wiadomosc: ''
      });

    const handleChange = e => {
        setForm({
          ...form,
          [e.target.name]: e.target.value
        });
    };

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

//   const navigate = useNavigate();
const [messageStatus, setMessageStatus] = useState(null); // Nowy stan lokalny

  const handleSubmit = async (event) => {
    event.preventDefault();
    console.log(username);


    fetch('http://localhost:3000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(form),
      })
      .then(response => response.json())
      .then(data => {
        console.log('Success:', data);
        setMessageStatus(data.message);
        // alert(`${data.message}`);
      })
      .catch((error) => {
        console.error('Error:', error);
      });



    // Miejsce na funkcję logowania, która powinna zwrócić promise
    // Na przykład:
    // try {
    //   await loginUser(username, password);
    //   navigate('/dashboard');
    // } catch (error) {
    //   // Obsłuż błąd logowania
    // }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Username:
        <input type="text" value={username} onChange={handleChange}/>
      </label>
      <label>
        Password:
        <input type="password" value={password} onChange={handleChange} />
      </label>
      <button type="submit">Login</button>
    </form>
  );
};

export default Login;
