import React, { useState, useEffect } from "react";

const Test2 = () => {
  const [divs, setDivs] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [rangeStart, setRangeStart] = useState(1);
  const [rangeEnd, setRangeEnd] = useState(5);

  // Gdy zakres się zmienia, zaktualizuj divs
  useEffect(() => {
    const newDivs = Array.from({length: rangeEnd - rangeStart + 1}, (_, i) => rangeStart + i);
    setDivs(newDivs);
  }, [rangeStart, rangeEnd]);

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleButtonClick = () => {
    const numDivsToProcess = parseInt(inputValue, 10);

    if (!isNaN(numDivsToProcess) && numDivsToProcess > 0) {
      let i = 0;
      let currentNum = rangeEnd + 1;

      const processDiv = () => {
        setDivs(prevDivs => {
          const newDivs = [...prevDivs];
          newDivs.pop(); // usuwamy jeden div
          newDivs.unshift(currentNum + i); // dodajemy nowy div na początek
          return newDivs;
        });

        i += 1;
        if (i < numDivsToProcess) {
          setTimeout(processDiv, 500); // wywołujemy tę samą funkcję z opóźnieniem, jeśli jeszcze nie przetworzyliśmy wszystkich divów
        } else {
          setRangeEnd(currentNum + i - 1); // zaktualizuj koniec zakresu
        }
      };

      processDiv(); // wywołujemy funkcję po raz pierwszy
    }
  };

  return (
    <div>
      <input type="number" placeholder="Początek zakresu" onChange={e => setRangeStart(Number(e.target.value))} />
      <input type="number" placeholder="Koniec zakresu" onChange={e => setRangeEnd(Number(e.target.value))} />
      <input type="number" value={inputValue} onChange={handleInputChange} />
      <button onClick={handleButtonClick}>Usunąć i dodać div</button>
      {divs.map((num, i) => (
        <div key={i} style={{ height: "50px", width: "50px", backgroundColor: "lightblue", margin: "5px" }}>
          {num}
        </div>
      ))}
    </div>
  );
};
export default Test2;
