import React, { useState, useEffect, useRef } from 'react';

const speed = 180;

function Losowanie2() {
  const [numbers, setNumbers] = useState(new Set());
  const [isRunning, setIsRunning] = useState(false); // New state to handle the generating status
  const intervalId = useRef(null); // useRef to store the interval id

  const generateRandomNumber = () => {
    let randomNumber;
    setNumbers(prevNumbers => {
      let newNumbers = new Set(prevNumbers);
      do {
        // Generate a random number from 1 to 20 inside the loop
        randomNumber = Math.floor(Math.random() * 20) + 1;
      } while (newNumbers.has(randomNumber)); // Repeat until we get a unique number

      if (newNumbers.size === 5) {
        // Remove the first element of the array and add the new number at the end
        const firstNumber = Array.from(newNumbers)[0];
        newNumbers.delete(firstNumber);
      }
      newNumbers.add(randomNumber);
      return newNumbers;
    });
  }

  const handleButtonClick = () => {
    setIsRunning(!isRunning);
  }

  useEffect(() => {
    if (isRunning) {
      intervalId.current = setInterval(generateRandomNumber, speed);
    } else {
      // If the generating is running, stop the generating
      if (intervalId.current) clearInterval(intervalId.current);
    }
  }, [isRunning]); // Add isRunning to the dependency array

  // Dodaj to useEffect do wygenerowania trzech losowych numerów na początku
  useEffect(() => {
    for(let i = 0; i < 3; i++) {
      generateRandomNumber();
    }
  }, []);

  return (
    <div className='zaLeb'>
      <button onClick={handleButtonClick}>{isRunning ? 'STOP' : 'START'}</button>
      {Array.from(numbers).map((number, index) => (
         <div key={index} 
         className={index === 2 ? 'middle-div' : (index === 1 ? 'gora-class' : (index === 3 ? 'dol-class' : ''))}>{number}</div>
      ))}
    </div>
  );
}

export default Losowanie2;
