import React, { useState, useEffect } from 'react';
import './App.css';

const Div = ({ startAnimation, setAnimationDone }) => {
  const [number, setNumber] = useState(null);
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    if (startAnimation) {
      setIsAnimating(true);
    }
  }, [startAnimation]);

  useEffect(() => {
    if (isAnimating) {
      const interval = setInterval(() => {
        const num = Math.floor(Math.random() * 49 + 1);
        if (number !== num) setNumber(num);
      }, 100);

      setTimeout(() => {
        clearInterval(interval);
        setIsAnimating(false);
        setAnimationDone(true);
      }, 3000);
    }
  }, [isAnimating, number, setAnimationDone]);

  return (
    <div 
      style={{
        width: '50px',
        height: '50px',
        border: '1px solid black',
        backgroundColor: '#eee',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      {number}
    </div>
  );
}

const Siatka = () => {
  const [startAnimation, setStartAnimation] = useState(0);
  const [animationDone, setAnimationDone] = useState(false);
  const grid = [];

  const handleClick = () => {
    setStartAnimation(startAnimation + 1);
  }

  useEffect(() => {
    if(animationDone){
      setStartAnimation(startAnimation + 1);
      setAnimationDone(false);
    }
  }, [animationDone, startAnimation]);

  for (let i = 0; i < 6; i++) {
    if(i < startAnimation){
      grid.push(
        <Div
          key={i}
          startAnimation={true}
          setAnimationDone={setAnimationDone}
        />
      );
    } else {
      grid.push(
        <Div
          key={i}
          startAnimation={false}
          setAnimationDone={setAnimationDone}
        />
      );
    }
  }

  return (
    <div className="grid" style={{userSelect: 'none', display: 'flex', gap: '10px'}}>
      {grid}
      <button onClick={handleClick} style={{marginTop: '20px'}}>Losuj liczby</button>
    </div>
  );
};

export default Siatka;
